------------------ PRG MASTER TABLE ------------------

CREATE TABLE PRG_MASTER
(
    "ID"           NUMBER(20, 0) GENERATED BY DEFAULT ON NULL AS IDENTITY,
    "PRG_ID"       VARCHAR2(10 BYTE)                                  NOT NULL ENABLE,
    "PRG_DESC"     VARCHAR2(150 BYTE),
    "MODULE_CD"    VARCHAR2(5 BYTE)                                   NOT NULL ENABLE,
    "TYPE"         VARCHAR2(1 BYTE)                                   NOT NULL ENABLE,
    "FILE_PATH"    VARCHAR2(100 BYTE),
    "REMARK"       VARCHAR2(200 BYTE),
    "HEADING"      VARCHAR2(1 BYTE),
    "SEQ"          NUMBER(3, 0),
    "SPECIAL_TYPE" VARCHAR2(1 BYTE),
    "DISPLAY"      VARCHAR2(1 BYTE),
    "VERSION"      NUMBER                   DEFAULT 0                 NOT NULL,
    STATUS         INTEGER                  DEFAULT 1                 NOT NULL,
    CREATED_BY     VARCHAR2(200)            DEFAULT 'system'          NOT NULL,
    CREATED_AT     TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP NOT NULL,
    UPDATED_BY     VARCHAR2(200)            DEFAULT 'system'          NOT NULL,
    UPDATED_AT     TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP NOT NULL
);

CREATE UNIQUE INDEX PRG_MASTER_PK ON PRG_MASTER ("ID");

ALTER TABLE PRG_MASTER
    ADD CONSTRAINT PRG_MASTER_PK PRIMARY KEY ("ID")
        USING INDEX PRG_MASTER_PK ENABLE;