----------------------------------------------- PRODUCT -----------------------------------------------

CREATE TABLE PRODUCT
(
    "COMPANY_CODE"         VARCHAR2(3 BYTE)  NOT NULL ENABLE,
    "PLANT_NO"             NUMBER(3, 0)      NOT NULL ENABLE,
    "TYPE"                 VARCHAR2(10 BYTE) NOT NULL ENABLE,
    "SUB_TYPE"             VARCHAR2(10 BYTE) NOT NULL ENABLE,
    "DESCRIPTION"          VARCHAR2(30 BYTE),
    "SUB_TYPE_DESCRIPTION" VARCHAR2(30 BYTE),
    "PRODUCT_LEVEL"        VARCHAR2(1 BYTE)  NOT NULL ENABLE,
    "PRODUCT_GROUP"        VARCHAR2(10 BYTE) NOT NULL ENABLE,
    "GROUP_DESCRIPTION"    VARCHAR2(30 BYTE),
    "LAST_GENERATED_NO"    NUMBER(10, 0),
    "WIP_TRACKING"         VARCHAR2(1 BYTE),
    "PRODUCT_TYPE"         VARCHAR2(1 BYTE),
    "PREFIX"               VARCHAR2(10 BYTE),
    "PREFIX_LENGTH"        NUMBER(3, 0),
    "POSTFIX"              VARCHAR2(10 BYTE),
    "POSTFIX_LENGTH"       NUMBER(3, 0),
    "CATEGORY_CODE"        VARCHAR2(3 BYTE),
    "CATEGORY_SUB_CODE"    VARCHAR2(5 BYTE),
    "MRP_STATUS"           VARCHAR2(1 BYTE),
    "AUTO_BOM"             VARCHAR2(1 BYTE)
);

CREATE UNIQUE INDEX PRODUCT_PK ON PRODUCT ("COMPANY_CODE", "PLANT_NO", "TYPE", "SUB_TYPE");

ALTER TABLE PRODUCT
    ADD CONSTRAINT PRODUCT_PK PRIMARY KEY ("COMPANY_CODE", "PLANT_NO", "TYPE", "SUB_TYPE")
        USING INDEX PRODUCT_PK ENABLE;

