------------ ITEM table creation ------------------
CREATE TABLE "ITEM"
(
    "ID"                NUMBER(20, 0) GENERATED BY DEFAULT ON NULL AS IDENTITY,
    "COMPANY_CODE"      VARCHAR2(3 BYTE) NOT NULL ENABLE,
    "PLANT_NO"          NUMBER(3,0) NOT NULL ENABLE,
    "ITEM_NO"           VARCHAR2(15 BYTE) NOT NULL ENABLE,
    "VERSION"           NUMBER NOT NULL ENABLE,
    "LOC"               VARCHAR2(5 BYTE) NOT NULL ENABLE,
    "PART_NO"           VARCHAR2(60 BYTE),
    "DESCRIPTION"       VARCHAR2(1000 BYTE),
    "CATEGORY_CODE"     VARCHAR2(10 BYTE),
    "CATEGORY_SUB_CODE" VARCHAR2(10 BYTE),
    "CATEGORY_GROUP"    VARCHAR2(2 BYTE),
    "MANUFACTURER"      VARCHAR2(30 BYTE),
    "UOM"               VARCHAR2(3 BYTE),
    "DRAWING"           VARCHAR2(10 BYTE),
    "ISSUE_NO"          VARCHAR2(30 BYTE),
    "SPEC_NO"           VARCHAR2(30 BYTE),
    "QA"                VARCHAR2(10 BYTE),
    "REV"               VARCHAR2(3 BYTE),
    "CLASS"             VARCHAR2(3 BYTE),
    "SOURCE"            VARCHAR2(1 BYTE),
    "ORDER_POLICY"      VARCHAR2(3 BYTE),
    "ALTERNATE"         VARCHAR2(15 BYTE),
    "PRODUCT_GROUP"     VARCHAR2(10 BYTE),
    "BUYER"             VARCHAR2(20 BYTE),
    "SUPPLIER_CODE"     VARCHAR2(10 BYTE),
    "ACCOUNT_CODE"      VARCHAR2(10 BYTE),
    "COUNT_CODE"        VARCHAR2(1 BYTE),
    "DIV_CODE"          VARCHAR2(2 BYTE),
    "DEPT_CODE"         VARCHAR2(3 BYTE),
    "REMARKS"           VARCHAR2(2000 BYTE),
    "QOH"               NUMBER(16,4),
    "BALBF_QTY"         NUMBER(16,4),
    "LAST_BALBF_QTY"    NUMBER(16,4),
    "PRICEA"            NUMBER(16,4),
    "PRICEB"            NUMBER(16,4),
    "PRICEC"            NUMBER(16,4),
    "REORDER"           NUMBER(16,4),
    "LEADTIME"          NUMBER(3,0),
    "LOT"               NUMBER(16,4),
    "YTD_PURCH"         NUMBER(16,4),
    "YTD_PROD"          NUMBER(16,4),
    "YTD_SALE"          NUMBER(16,4),
    "YTD_ISSUE"         NUMBER(16,4),
    "YTD_RECEIPT"       NUMBER(16,4),
    "OBSOLETE_CODE"     VARCHAR2(1 BYTE),
    "OBSOLETE_QTY"      NUMBER(16,4),
    "BATCH_NO"          NUMBER(8,0),
    "ORDER_QTY"         NUMBER(16,4),
    "PICKED_QTY"        NUMBER(16,4),
    "PRODN_RESV"        NUMBER(16,4),
    "SALES_RESV"        NUMBER(16,4),
    "ADV_PR_RESV"       NUMBER(16,4),
    "SAFETY_STOCK"      NUMBER(16,4),
    "STD_SETUP"         NUMBER(16,4),
    "STD_MATERIAL"      NUMBER(16,4),
    "STD_LABOR"         NUMBER(16,4),
    "STD_BURDEN"        NUMBER(16,4),
    "STD_SUBCONTRACT"   NUMBER(16,4),
    "STD_MACHINE"       NUMBER(16,4),
    "FREIGHT"           NUMBER(16,4),
    "HANDLING"          NUMBER(16,4),
    "CUSTOM_DUTY"       NUMBER(16,4),
    "LAST_PUR_PRICE"    NUMBER(16,4),
    "LAST_PUR_DATE"     DATE,
    "LAST_TRAN_DATE"    DATE,
    "LAST_COUNT_DATE"   DATE,
    "COST_VARIANCE"     NUMBER(16,4),
    "QC_FLAG"           VARCHAR2(1 BYTE),
    "COUNT_QOH"         NUMBER(16,4),
    "COUNT_TAG"         VARCHAR2(10 BYTE),
    "COUNT_UPD_IND"     VARCHAR2(1 BYTE),
    "DIMENSION"         VARCHAR2(60 BYTE),
    "ROHS_STATUS"       VARCHAR2(1 BYTE),
    "RPC_RESV"          NUMBER(16,4),
    "MRV_RESV"          NUMBER(16,4),
    "MSL_CODE"          VARCHAR2(3 BYTE),
    "OPEN_CLOSE"        VARCHAR2(1 BYTE),
    "CLOSE_DATE"        DATE,
    "OBSOLETE_ITEM"     VARCHAR2(15 BYTE),
    "OBSOLETE_DATE"     DATE,
    "REF_URL"           VARCHAR2(200 BYTE),
    "REQUESTOR"         VARCHAR2(30 BYTE),
    "STORAGE_SHELF"     VARCHAR2(15 BYTE),
    "STATUS"            INTEGER NOT NULL,
    "CREATED_BY"        VARCHAR2(200 BYTE) NOT NULL ENABLE,
    "CREATED_AT"        TIMESTAMP (6) WITH TIME ZONE NOT NULL ENABLE,
    "UPDATED_BY"        VARCHAR2(200 BYTE) NOT NULL ENABLE,
    "UPDATED_AT"        TIMESTAMP (6) WITH TIME ZONE NOT NULL ENABLE
);

CREATE UNIQUE INDEX "ITEM_PK" ON "ITEM" ("ID");
ALTER TABLE "ITEM" ADD CONSTRAINT "ITEM_PK" PRIMARY KEY ("ID") USING INDEX "ITEM_PK"  ENABLE;

CREATE INDEX "ITEM_IDX" ON "ITEM" ("PART_NO");
CREATE INDEX "ITEMNO_IDX" ON "ITEM" ("ITEM_NO");

CREATE UNIQUE INDEX UNIQUE_ITEM_IDX ON ITEM ("COMPANY_CODE", "PLANT_NO", "ITEM_NO");

------------ ITEMLOC table creation ------------------
