     CONSTRAINT "ITEMLOC_FK" FOREIGN KEY ("ITEM_ID")
         REFERENCES "ITEM" ("ID") ENABLE
);

CREATE UNIQUE INDEX ITEMLOC_PK ON ITEMLOC ("ID");

ALTER TABLE "ITEMLOC" ADD CONSTRAINT "ITEMLOC_PK" PRIMARY KEY ("ID")
    USING INDEX "ITEMLOC_PK"  ENABLE;

CREATE INDEX "ITEMLOC_IDX" ON "ITEMLOC" ("PART_NO", "LOC");

CREATE UNIQUE INDEX UNIQUE_ITEMLOC_IDX ON ITEMLOC ("COMPANY_CODE", "PLANT_NO", "ITEM_NO", "LOC");

