
CREATE TABLE DEFAULT_CODE
(	"COMPANY_CODE" VARCHAR2(3 BYTE) NOT NULL ENABLE,
     "PLANT_NO" NUMBER(3,0) NOT NULL ENABLE,
     "DEFAULT_CODE" VARCHAR2(30 BYTE) NOT NULL ENABLE,
     "DATA_BLOCK" VARCHAR2(30 BYTE) NOT NULL ENABLE,
     "CODE_NAME" VARCHAR2(30 BYTE) NOT NULL ENABLE,
     "VERSION" NUMBER DEFAULT 0 NOT NULL,
     "DB_STATUS" VARCHAR2(1 BYTE),
     "DEFAULT_VALUE" VARCHAR2(100 BYTE),
     "DESCRIPTION" VARCHAR2(100 BYTE),
     STATUS INTEGER DEFAULT 1 NOT NULL,
     CREATED_BY VARCHAR2(200) DEFAULT 'system' NOT NULL,
     CREATED_AT TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP NOT NULL,
     UPDATED_BY VARCHAR2(200) DEFAULT 'system' NOT NULL,
     UPDATED_AT TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP NOT NULL
);

CREATE UNIQUE INDEX DEFAULT_ITEM_PK ON DEFAULT_CODE ("COMPANY_CODE", "PLANT_NO", "DEFAULT_CODE");
ALTER TABLE DEFAULT_CODE ADD CONSTRAINT DEFAULT_ITEM_PK PRIMARY KEY ("COMPANY_CODE", "PLANT_NO", "DEFAULT_CODE")
    USING INDEX DEFAULT_ITEM_PK ENABLE;


CREATE TABLE DEFAULT_CODE_DET
