CREATE UNIQUE INDEX DEFAULT_ITEM_DET_PK ON DEFAULT_CODE_DET ("COMPANY_CODE", "PLANT_NO", "DEFAULT_CODE", "SEQ_NO");
ALTER TABLE DEFAULT_CODE_DET ADD CONSTRAINT DEFAULT_ITEM_DET_PK PRIMARY KEY ("COMPANY_CODE", "PLANT_NO", "DEFAULT_CODE", "SEQ_NO")
    USING INDEX DEFAULT_ITEM_DET_PK ENABLE;




